package hu.qgears.tools.build.compiler;

import javax.tools.Diagnostic;
import javax.tools.DiagnosticCollector;
import javax.tools.FileObject;
import javax.tools.JavaFileManager.Location;
import javax.tools.JavaFileObject;
import javax.tools.JavaFileObject.Kind;

public interface ICompileCallback {
	/**
	 * Process all diagnostics generated by the compiler (errors, warnings, etc.)
	 * @param diagnostics
	 */
	default void processDiagnostics(DiagnosticCollector<JavaFileObject> diagnostics)
	{
		for(Diagnostic<? extends JavaFileObject> d: diagnostics.getDiagnostics())
		{
			System.err.println(""+d);
		}
	}
	/**
	 * Log the event when an output file is created in case of in-mempory
	 * compilation.
	 * @param location
	 * @param className
	 * @param kind
	 * @param sibling
	 */
	default void getFileForOutput(Location location, String className, Kind kind, FileObject sibling) {}
}
